import React, { useState, useEffect } from 'react';
import '../css/Header.css';
import logo from '../image/logo.svg';
import bottomimg from '../image/Ellipse 21.svg';
import topimg from '../image/myprofile.svg';
import { getUser,getFolder } from '../api';
function Header(){
    const [userInfo, setInfo] = useState({name:'',email:'',profileImageSource: ''});
    const [folderInfo,setFolder] = useState('');
    useEffect(()=>{
        async function getData() {
            const userData = await getUser();
            setInfo(userData);
        }
        async function getFolderData(){
            const folderData = await getFolder();
            setFolder
        }
        getData();
    },[])
    const username = userInfo.name;
    const useremail =  userInfo.email;
    const userprofile = userInfo.profileImageSource;
    

    return (
        <div className="headerContainer">
            <div className="headersub">
                <div className="subcontainer">
                    <a href="./">
                        <img src={logo} alt="homelogo"></img>
                    </a>
                    <div className="userInfo">
                        <div className="headerimgContainer">
                            <img src={bottomimg} id="bottomimg" alt = "profile"></img>
                            <img src={topimg} id="topimg" alt = "profile"></img>
                        </div>
                        <span>{useremail}</span>
                    </div>
                </div>
            </div>
            <div className ="headermain">
                <div className = "maininfo">
                    <div className="frameinfo">
                        <div className="sharedlogo">
                            <img src={userprofile}></img>
                        </div>
                        <span>{username}</span>
                    </div>
                    <div className="bookmarkinfo">
                        <span>⭐️ 즐겨찾기</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Header;
