import React, { useState, useEffect } from 'react';
import '../css/Footer.css';
import logo from '../image/logo.svg';
import { getUser } from '../api';
function Header(){
    const [userInfo, setInfo] = useState({name:'',email:'',profileImageSource: ''});
    
    useEffect(()=>{
        async function getData() {
            const userData = await getUser();
            setInfo(userData);
        }
        getData();
    },[])
    const username = userInfo.name;
    const useremail =  userInfo.email;
    const userprofile = userInfo.profileImageSource;
    console.log(userInfo);

    return (
        <div className="header-container">
            <div className="headersub">
                <div className="subcontainer">
                    <a href="./">
                        <img src={logo} alt="homelogo"></img>
                    </a>
                    <div className="user-info">
                        <div className="headerimg-container">
                            <img src ={userprofile} id="userImg" alt = "profile"></img>
                        </div>
                        <span>{useremail}</span>
                    </div>
                </div>
            </div>
            {/* <div className ="headermain">
                <div className = "maininfo">
                    <div className="frameinfo">
                        <div className="sharedlogo">
                            <img src="./imgshared/white.svg"></img>
                        </div>
                        <span>@코드잇</span>
                    </div>
                    <div className="bookmarkinfo">
                        <span>⭐️ 즐겨찾기</span>
                    </div>
                </div>
            </div> */}
        </div>
    );
}

export default Header;
